import React, {Component} from 'react';
import ReactDOM from 'react-dom';
import './App.css';
import axios from 'axios';
import {
  Button,
  Header,
  Icon,
  Image,
  Menu,
  Segment,
  Sidebar
} from "semantic-ui-react";







export default class App extends Component {

  constructor()
{
super();
this.state={
  devices:[]
};


}
state = { visible: false };

handleSidebarHide = () => this.setState({ visible: false });
handleSidebarToggle = () =>
  this.setState(prevState => ({ visible: !prevState.visible }));







componentWillMount()
{
  axios.get('http://127.0.0.1:8000/api/device').then(response =>
  {
    this.setState({
      devices:response.data
    });
  }).catch(errors => {
    console.log(errors);
  })

}

  render() {
    const { visible } = this.state;

    return(
/*<div className="container">
  <button class="ui primary button" >
  Open details view
  </button>
  <div class="ui simple sidebar vertical menu right uncover visible">

    </div>
</div>*/
<div>
        <Button.Group>
          <Button onClick={this.handleSidebarToggle}>Toggle sidebar</Button>
        </Button.Group>
        <Sidebar.Pushable as={Segment}>
          <Sidebar
            as={Menu}
            animation="overlay"
            icon="labeled"
            inverted
            onHide={this.handleSidebarHide}
            vertical
            visible={visible}
            width="thin"
          >
            <Menu.Item as="a">
              <Icon name="home" />
              Home
            </Menu.Item>
            <Menu.Item as="a">
              <Icon name="gamepad" />
              Games
            </Menu.Item>
            <Menu.Item as="a">
              <Icon name="camera" />
              Channels
            </Menu.Item>
          </Sidebar>

          <Sidebar.Pusher>
            <Segment basic>
              <Header as="h3">Application Content</Header>
              <Image src="https://react.semantic-ui.com/images/wireframe/paragraph.png" />
            </Segment>
          </Sidebar.Pusher>
        </Sidebar.Pushable>
      </div>
);



  }

}
