import React, {Component} from 'react';
import ReactDOM from 'react-dom';
import './App.css';
import axios from 'axios';
import {
  Button,
  Header,
  Icon,
  Image,
  Menu,
  Segment,
  Sidebar
} from "semantic-ui-react";







export default class App extends Component {

  constructor()
{
super();
this.state={
  devices:[]
};


}
state = { visible: false };

handleSidebarHide = () => this.setState({ visible: false });
handleSidebarToggle = () =>
  this.setState(prevState => ({ visible: !prevState.visible }));







componentDidMount()
{
  axios.get('http://127.0.0.1:8000/api/device').then(response =>
  {

    this.setState({

      devices:response.data
    });
  }).catch(errors => {
    console.log(errors);
  })

}

  render() {


    const { visible } = this.state;
if(this.state.devices[0]){
  return(

<div style={{height: '100vh'}}>

        <Button primary onClick={this.handleSidebarToggle}>Toggle sidebar</Button>

      <Sidebar.Pushable >
        <Sidebar
          as={Menu}
          animation="overlay"
          direction="right"
          onHide={this.handleSidebarHide}
          vertical
          width="wide"
          visible={visible}

        >
        <Header as='h1'>First Header</Header>
        <Header as='h2'>Second Header</Header>
        <Header as='h4'>third Header</Header>
          <div class="ui disabled input">
  <input type="text" placeholder={this.state.devices[0]} disabled="" tabindex="-1" />
</div>
        <Header as='h4'>fourth Header</Header>
        <Header as='h4'>fifth Header</Header>

        </Sidebar>


      </Sidebar.Pushable>
    </div>
);
return Null;
}




  }

}
